[(lambda batter: (lambda percentage: print(f"{batter[0]}={0 if not percentage else (int(sum(percentage) / len(percentage) * 1000 + 0.5) / 1000):.3f}"))([['K', '1B', '2B', '3B', 'HR'].index(hit) for hit in batter[1].split('\n') if not hit in ['BB', '']]))(input().split(':')) for i in [0]*int(input())]